                tid = result.data.id;
                layer.open({
                    // anim: 'up',
                    title: [result.data.title,'background-color: #FF4351; color:#fff;'],
                    content: result.data.content,
                    btn: [`<?php echo lang('confirm'); ?>`],
                    shadeClose: false,
                    yes: function(index) {
                        if (result.data.url)
                            location.href = result.data.url
                        layer.close();
                    },
                    no: function() {
                        layer.close();
                    }
                });
            }
        }
    });
}

