<div class="layui-card layui-bg-gray"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header layui-anim layui-anim-fadein notselect"><span class="layui-icon layui-icon-next font-s10 color-desc margin-right-5"></span><?php echo htmlentities((isset($title) && ($title !== '')?$title:'')); ?><div class="pull-right"></div></div><?php endif; ?><div class="layui-card-body layui-anim layui-anim-upbit"><div class="think-box-shadow"><table class="layui-table margin-top-15" lay-skin="line"><?php if(!(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty()))): ?><thead><tr><th class='text-left nowrap'>ID</th><th class='text-left nowrap'>名称</th><th class='text-left nowrap'>图标</th><th class='text-left nowrap'>标识</th><th class='text-left nowrap'>最小金额</th><th class='text-left nowrap'>最大金额</th><th class='text-left nowrap'>类型</th><th class='text-left nowrap'>状态</th><th class='text-left nowrap'>提现资料</th><?php if(auth("edit_home_msg")): ?><th class='text-left nowrap'>操作</th><?php endif; ?></tr></thead><?php endif; ?><tbody><?php foreach($list as $key=>$vo): ?><tr><td class='text-left nowrap'><?php echo htmlentities($vo['id']); ?></td><td class='text-left nowrap'><?php echo htmlentities($vo['name']); ?></td><td class='text-left nowrap' style="font-size: 65px;"><?php if($vo['ico_type'] == '1'): ?><?php echo $vo['ico']; else: if($vo['ico']): ?><a data-dbclick data-title="查看图片" data-modal='<?php echo url("admin/index/picinfo"); ?>?pic=<?php echo htmlentities($vo['ico']); ?>'><img src="<?php echo htmlentities($vo['ico']); ?>" style="width:150px;"></a><?php else: ?><img src="<?php echo htmlentities($vo['ico']); ?>" style="width:150px;"><?php endif; ?><?php endif; ?></td><td class='text-left nowrap'><?php echo htmlentities($vo['name2']); ?></td><td class='text-left nowrap'><?php echo htmlentities($vo['min']); ?></td><td class='text-left nowrap'><?php echo htmlentities($vo['max']); ?></td><td class='text-left nowrap'><?php if($vo['recharge'] == '0'): ?><span class="color-red">提现</span><?php else: ?><span class="color-green">充值</span><?php endif; ?></td><td class='text-left nowrap'><?php if($vo['status'] == '0'): ?><span class="color-red">已禁用</span><?php else: ?><span class="color-green">使用中</span><?php endif; ?></td><td class='text-left nowrap'><?php if($vo['withdrawal_user'] == '0'): ?><span class="color-red">不需要</span><?php else: ?><span class="color-green">需要</span><?php endif; ?></td><td class='text-left nowrap'><?php if(auth("edit_home_msg")): ?><a data-dbclick class="layui-btn layui-btn-xs" data-title="编辑" data-open='<?php echo url("admin/pay/edit"); ?>?id=<?php echo htmlentities($vo['id']); ?>'>编 辑</a><?php if($vo['status'] == 1): ?><a class="layui-btn layui-btn-warm layui-btn-xs" data-action="<?php echo url('status'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>;status#0" data-csrf="<?php echo systoken('admin/pay/status'); ?>">禁 用</a><?php elseif($vo['status'] == 0): ?><a class="layui-btn layui-btn-warm layui-btn-xs" data-action="<?php echo url('status'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>;status#1" data-csrf="<?php echo systoken('admin/apy/status'); ?>">启 用</a><?php endif; ?><?php endif; ?></td></tr><?php endforeach; ?></tbody></table></div><script>
function del_message(id) {
    layer.confirm("确认要删除吗，删除后不能恢复", { title: "删除确认" }, function(index) {
        $.ajax({
            type: 'POST',
            url: "<?php echo url('del_banner'); ?>",
            data: {
                'id': id,
            },
            headers: {
                'User-Token-Csrf': "<?php echo systoken('admin/help/del_banner'); ?>"
            },
            success: function(res) {
                layer.msg(res.info, { time: 2500 });
                location.reload();
            }
        });
    }, function() {});
}
</script></div></div>