</script><script type="text/javascript">var submit = true,
    status = false,
    timer = null,
    ajaxT = null,
    lay = 0;

$(function() {
    hui.scrollNews(scrollnew1);
    // hui.scrollNews(scrollnew2, 8000);
    get_data();
    setInterval(function() { get_data(); }, 3000);
})
var cid = 0;

function get_data() {
    $.ajax({
        url: urlPost("rot_order/index"),
        type: "POST",
        dataType: "JSON",
        // data: { type: cid },
        success: function(res) {
            console.log(res)
            var data = res.data;
            if (res.code == 0) {
                cid = data.cate.id;
                $('.cate-name').html(data.cate.name);
                $('.cate-bili').html(data.cate.bili * 100);
                $('.day_deal').html(data.day_deal);
                // $('.hui-common-title-txt').attr('title',data.day_deal);
                $('.lock_deal').html(data.lock_deal);
                $('.price').html(data.price);
                $('.day_d_count').html(`${data.day_d_count}(${data.day_d_count_c})`);
                $('.day_l_count').html(data.day_l_count);
                $('.team_num').html(data.team_num);
            }
        },
        error: function(err) { console.log(err) }
    })
}
$('.self_btn').click(function() {
    
    tt1 = 1260 * -1;
    console.log(tt1);
    $('.animation-ease').eq(0).css('transform','translate3d(0px, '+tt1+'px, 0px)');
    $('.animation-ease').eq(1).css('transform','translate3d(0px, '+tt1+'px, 0px)');
    $('.animation-ease').eq(2).css('transform','translate3d(0px, '+tt1+'px, 0px)');
    
    layer.open({
        content: `<?php echo lang('Scrapping orders'); ?>...<br/><div class="loading"></div>`,
        btn: ["<?php echo lang('Stop rushing'); ?>", ],
        shadeClose: false,
        yes: function(index) {
            lay = index;
            clearTimeout(ajaxT);
            QS_toast.show("<?php echo lang('Stop ordering'); ?>", true);
            layer.close(index)
        }
    });

    ajaxT = setTimeout(function() {
        $.ajax({
            url: "<?php echo url('submit_order'); ?>",
            type: "POST",
            dataType: "JSON",
            data: { cid: cid },
            success: function(res) {
                console.log(res)
                status = true;
                if (res.code == 0) {
                    QS_toast.show(res.info, true);
                    layer.close(lay)
                    var timer = setTimeout(function() {
                        location.href = res.url
                    }, 1800)
                } else {
                    if (res.code == 5) {
                        layer.open({
                            anim: 'up',
                            content: res.info,
                            btn: [`<?php echo lang('confirm'); ?>`, `<?php echo lang('cancel'); ?>`],
                            shadeClose: false,
                            yes: function(index) {
                                if (res.url)
                                    location.href = res.url
                                layer.close();
                            },
                            no: function() {
                                layer.close();
                            }
                        });
                    } else {
                        QS_toast.show(res.info, true);
                        if (res.url) {
                            var linkTime = setTimeout(function() {
                                location.href = res.url
                            }, 3000);
                        } else {
                            var timer = setInterval(function() {
                                location.href = location.href
                            }, 3000);
                        }
                    }
                    layer.close(lay)
                }
            },
            error: function(err) { console.log(err) }
        })
    }, 3000)
})
</script></body></html>